def n_caracteres(e)
    p = e.scan(/\w/)
    return p.length
end

def n_caractere_max(e)
    v = 0
    for element in e
        p = element.length
        if v < p 
            array_de_merde = tri_nbr(e)
            v = p
        end
    end
    return v
end

def array_sort(e)
    trié_wsh = e.sort_by(&:downcase)
    return trié_wsh
end

def tri_nbr(e)
    trié_par_taille = e.sort_by(&:length)
    return trié_par_taille
end

def capitale (e)
    p = e.scan(/\w/).first
    p.match?(/\A[A-Z]/)
end

def nbr_cinq(e)
    konteur = 0
    for element in e
    if n_caracteres(element) == 5
        konteur = konteur + 1
    end
    end
    return konteur
end

def check_capitale(e)
    kontheur = 0
    for element in e
    if capitale(element) == true 
        kontheur = kontheur + 1
    end
    end
    return kontheur
end

def position_handle(e, handle)
    position = e.index(handle)
    return position
end

def tri_et_groupe_par_longueur(e)
    groupes = e.group_by { |handle| handle.length }
    groupes.each { |k, v| groupes[k] = v.sort }
    return groupes
end

